export const allDestinations: Destination[] = rawDestinations.map(dest => {
    const { flag, icon } = getFlagOrIcon(dest.label, dest.type);
    return {
        ...dest,
        value: dest.label.toLowerCase().replace(/[^a-z0-9]+/g, '-').replace(/^-+|-+$/g, ''),
        name: dest.label,
        flag,
        icon,
    };
}).sort((a, b) => a.label.localeCompare(b.label));

