const getFlagOrIcon = (label: string, type: DestinationType): { flag?: string, icon?: React.ElementType } => {
    if (type === 'Regi√≥n') {
        if (label.includes('Am√©rica') || label.includes('Caribe') || label.includes('Norteam√©rica')) return { icon: MapPin };
        if (label.includes('Europa') || label.includes('Balcanes') || label.includes('Escandinavia')) return { icon: Globe };
        if (label.includes('Asia') || label.includes('Oriente Medio')) return { icon: Earth };
        if (label.includes('√Åfrica') || label.includes('Ocean√≠a') || label.includes('Global')) return { icon: Globe };
        return { icon: Globe };
    }
    
    if (type === 'Pa√≠s' && countryFlags[label]) {
        return { flag: countryFlags[label] };
    }
    if (type === 'Ciudad' && cityFlags[label]) {
        return { flag: cityFlags[label] };
    }
    
    return {};
};

